import React, { useState } from "react";
import Sidebar from "../components/sidebar";
import { Container, Row, Col, Form , Button} from "react-bootstrap";
import DailyIntakeTable from "../components/dailyintaketable";

const Dailyintakes = () => {
    // Sample data
    const dataTemplate = {
        Userid: "Data",
        Date: "Data",
        Hrsofsleep: "Data",
        Glassesofwater: "Data",
        Stepstaken: "Data",
        Caloricintake: "Data",
        Caloricdiversity: "Data",
        HALE: "Data",
        PHD: "Data",
        Mealid: "Data",
      };
    
      const data = Array.from({ length: 10 }, () => ({ ...dataTemplate }));
  
    const [searchTerm, setSearchTerm] = useState(""); // State to hold the search term
  
    // Function to handle search term change
    const handleSearchChange = (event) => {
      setSearchTerm(event.target.value);
    };
  
    // Filter the data based on the search term
    const filteredData = data.filter((item) =>
      Object.values(item).some((value) =>
        value.toLowerCase().includes(searchTerm.toLowerCase())
      )
    );
  
    return (
      <Container fluid>
        <Row>
          <Col md={2}>
            <Sidebar />
          </Col>
          <Col md={10}>
            <div>
              {/* Search Bar with Search Button */}
              <Form inline>
                <Form.Group controlId="searchBar">
                  <Form.Control
                    type="text"
                    placeholder="Search..."
                    value={searchTerm}
                    onChange={handleSearchChange}
                  />
                </Form.Group>
                <Button variant="primary" onClick={() => setSearchTerm("")}>
                  Clear
                </Button>
              </Form>
              {/* Daily Intake Table */}
              <DailyIntakeTable data={filteredData} />
            </div>
          </Col>
        </Row>
      </Container>
    );
  };
  
  export default Dailyintakes;
